mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail:VARCHAR}, manufacture_day = #{manuDate:VARCHAR} , price = #{price}, image_file = #{fileName:VARCHAR}, prod_num = #{prodNum}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT DISTINCT p.* FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND p.prod_no = #{searchKeyword}
AND p.prod_name = #{searchKeyword}
AND p.price = #{searchKeyword}
ORDER BY p.reg_date DESC )inner_table
ORDER BY p.prod_name DESC )inner_table
ORDER BY p.prod_name ASC )inner_table
ORDER BY p.price DESC )inner_table
ORDER BY p.price ASC )inner_table
WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
